];

$outputDir = __DIR__ . '/../assets/img/og';

echo "Generating OG images for all tools...\n\n";

foreach ($tools as $toolId => $config) {
    $title = $i18nEn['tools'][$toolId]['toc_title'] ?? $toolId;
    $description = $i18nEn['seo'][$toolId]['meta_description'] ?? '';
    $icon = $config['icon'] ?? 'bi-tools';
    $category = $config['category'] ?? 'utilities';
    $color = $categoryColors[$category] ?? '#6c757d';

  // Shorten description if too long (max ~100 chars for good display)
    if (strlen($description) > 100) {
        $words = explode(' ', $description);
        $shortened = '';
        foreach ($words as $word) {
            if (strlen($shortened . ' ' . $word) > 100) {
                break;
            }
            $shortened .= ($shortened ? ' ' : '') . $word;
        }
        $description = $shortened . '...';
    }

  // Get icon path
    $iconPath = $iconPaths[$icon] ?? $iconPaths['bi-stars'];

  // Generate SVG
    $svg = generateOgSvg($title, $description, $color, $iconPath, $toolId);

  // Save to file
    $filename = $config['slug'] . '.svg';
    $filepath = $outputDir . '/' . $filename;
    file_put_contents($filepath, $svg);

    echo "✓ Generated: $filename ($category - $title)\n";
}

echo "\n✓ All OG images generated successfully!\n";

/**
 * Generate OG Image SVG based on template structure
 */
function generateOgSvg($title, $description, $color, $iconPath, $toolId)
{
  // Escape all dynamic values for SVG/XML context
    $title = htmlspecialchars($title, ENT_QUOTES | ENT_XML1, 'UTF-8');
    $description = htmlspecialchars($description, ENT_QUOTES | ENT_XML1, 'UTF-8');
    $color = htmlspecialchars($color, ENT_QUOTES | ENT_XML1, 'UTF-8');
    $iconPath = htmlspecialchars($iconPath, ENT_QUOTES | ENT_XML1, 'UTF-8');

  // Create unique gradient ID (sanitized)
    $gradientId = 'grad' . preg_replace('/[^a-zA-Z0-9]/', '', $toolId);

  // Calculate font sizes based on title length
    $titleFontSize = strlen($title) > 25 ? 56 : 68;
    $descFontSize = 42;

    return <<<SVG
<svg xmlns="http://www.w3.org/2000/svg" width="1200" height="630" viewBox="0 0 1200 630">
  <!-- Background -->
  <rect width="1200" height="630" fill="{$color}"/>
  
  <!-- Gradient Overlay -->
  <defs>
    <linearGradient id="{$gradientId}" x1="0%" y1="0%" x2="100%" y2="100%">
      <stop offset="0%" style="stop-color:{$color};stop-opacity:1" />
      <stop offset="100%" style="stop-color:#000000;stop-opacity:0.2" />
    </linearGradient>
  </defs>
  <rect width="1200" height="630" fill="url(#{$gradientId})"/>
  
  <!-- Icon (centered, large) -->
  <g transform="translate(450, 150)">
    <svg width="300" height="300" viewBox="0 0 16 16" fill="#ffffff" opacity="0.3">
      {$iconPath}
    </svg>
  </g>
  
  <!-- Text Content -->
  <text x="600" y="380" font-family="system-ui, -apple-system, sans-serif" font-size="{$titleFontSize}" font-weight="700" fill="#ffffff" text-anchor="middle">
    {$title}
  </text>
  <text x="600" y="450" font-family="system-ui, -apple-system, sans-serif" font-size="{$descFontSize}" font-weight="400" fill="#ffffff" text-anchor="middle" opacity="0.9">
    {$description}
  </text>
  
  <!-- Mini-Tools Branding (bottom) -->
  <text x="600" y="570" font-family="system-ui, -apple-system, sans-serif" font-size="24" font-weight="500" fill="#ffffff" text-anchor="middle" opacity="0.7">
    Mini-Tools Collection
  </text>
</svg>
SVG;
}
